USE [Form113]
CREATE TABLE Continents (
    idContinent integer NOT NULL,
    name character varying(20)
);


CREATE TABLE Regions (
    idRegion integer NOT NULL,
    name character varying(26) NOT NULL,
    idContinent integer NOT NULL
);


CREATE TABLE Pays (
    CodeIso3 character(3) NOT NULL,
    Name character(52) NOT NULL,
    surfacearea double precision NOT NULL,
    indepyear smallint,
    population integer NOT NULL,
    lifeexpectancy double precision,
    gnp double precision,
    gnpold double precision,
    localName character(45) NOT NULL,
    CodeIso2 character(2) NOT NULL,
    idRegion integer NOT NULL,
    idgovform integer NOT NULL,
    idheadofstate integer
);


ALTER TABLE dbo.Continents
ADD CONSTRAINT Continents_pk PRIMARY KEY (idContinent);

ALTER TABLE dbo.Regions
ADD CONSTRAINT Regions_pk PRIMARY KEY (idRegion);

ALTER TABLE dbo.Pays
ADD CONSTRAINT Pays_pk PRIMARY KEY (CodeIso3);

ALTER TABLE dbo.Villes
ADD CONSTRAINT Villes_pk PRIMARY KEY (idVille);

ALTER TABLE dbo.Regions
ADD CONSTRAINT Regions_Continents_fk FOREIGN KEY (idContinent) 
  REFERENCES dbo.Continents (idContinent) 
  ON UPDATE NO ACTION
  ON DELETE NO ACTION;
  
ALTER TABLE dbo.Pays
ADD CONSTRAINT Pays_Regions_fk FOREIGN KEY (idRegion) 
  REFERENCES dbo.Regions (idRegion) 
  ON UPDATE NO ACTION
  ON DELETE NO ACTION;
  
